import { useState } from "react";
import { Container } from "reactstrap";
import ProjectCard from "./components/Card";
import projects from "./projects.json";

import Login from "./views/Login";
// const LoginPage=()=><div>
// <h1>Login page</h1>
// </div>


function App() 
{
    let [showLoginPage,setShowLoginPage]=useState(true);

    const title=showLoginPage?"Login":"Projects";

    const togglePage=()=>setShowLoginPage(!showLoginPage);

    const onLoginSuccess=()=>{
      setShowLoginPage(false);
    }

  return (
          <div className="App">
            <h1>{title}</h1>
            <button onClick={()=>togglePage()}>See Projects Page</button>

            {
              showLoginPage?
            (<Login onLoginSuccess={onLoginSuccess}/>):
            (
                <div>
                {   projects.map((project)=>{
                    return(
                    <ProjectCard
                    imgSrcUrl={project.image_url}
                    cardTitle={project.name}
                    description={project.description}
                    imgAltText={project.name}
                    />
                    )
                  }
                  )
                }
                </div>
            )} 
          </div>
          );
}

export default App;
